//MARK: - Comment out line 9 if you don't want to support GameCenter's GamerTags
import GameKit

// MARK: 9.216 Million GamerTag Combinations
func gamerTagAutoGenerator() {
    let n = Int.random(in: 0..<randomNames.count)
    let r = Int.random(in: 1000...9999)

    if settings.player == nil {
        settings.player = "\(randomNames[n])\(r)"
    } else if let player = settings.player, player.isEmpty {
        settings.player = "\(randomNames[n])\(r)"
    } else if let c = settings.player?.count, c < 3 {
        settings.player = "\(randomNames[n])\(r)"
    }
    
    //For testing
    //settings.player = "BigRedHero"
    //print("GamerTag:", settings.player)
}

